import { Component, OnInit} from '@angular/core';
import { ItemService } from './restapi.service';
import { Notify } from 'notiflix';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit {

  public items2: any[];

  constructor(private itemService: ItemService, private titleService: Title) {

  }

  getData(id:string) {
    try
    {
      if (id != "")
      {
        this.itemService.getItems(id).subscribe(items => {this.items2 = items} );

        alert(this.items2);

        Notify.success('Accion realizada correctamente.');
      }
      else
      {
        Notify.warning('Existen campos vacios.');
      }

    }
    catch (exception)
    {
      Notify.failure('Error en obtener informacion del servidor. Codigo: ' + exception);
    }
  }

  ngOnInit() {
  }

  title = 'Express Server Test Frontend';
}
