import { Component, OnInit} from '@angular/core';
import { ItemService } from './restapi.service';
import { Notify } from 'notiflix';
import { Title } from '@angular/platform-browser';
import { NgxSpinnerService } from "ngx-spinner";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit {

  returnItem: any;

  constructor(private itemService: ItemService, titleService: Title, private spinner: NgxSpinnerService) {
    titleService.setTitle('Express Server Test Frontend');
  }

  getData(id:string) {

    this.spinner.show();

      setTimeout(() => {

        this.spinner.hide();
      }, 1000);

    try
    {
      var id2;

      if(id == ""){
        id2 = 0;
      }
      else{
        id2 = parseInt(id);
      }

      if (id2 > 0 && id2 < 1001)
      {
        this.itemService.getItems(id).subscribe(data => {
          this.returnItem = data;
          console.log(data)
        });

        Notify.success('Accion realizada correctamente.');
      }
      else if (id2 > 1000){
        Notify.failure('Valor superior a los registros.');
      }
      else
      {
        Notify.warning('Existen campos vacios.');
      }

    }
    catch (exception)
    {
      Notify.failure('Error en obtener informacion del servidor. Codigo: ' + exception);
    }
    finally{

    }
  }

  ngOnInit() {

  }
}
<input #intp type="number" class="form-control" id="inputPassword2" placeholder="ID">
<button class="btn btn-primary mb-2" (click)="getData(intp.value)">Solicitar</button>

<ngx-spinner
  bdColor="rgba(51,51,51,0.8)"
  size="medium"
  color="#fff"
  type="ball-scale-multiple"
>
  <p style="font-size: 20px; color: white">Loading...</p>
</ngx-spinner>

<ul class="list-group">
    <li class="list-group-item" *ngFor="let item of returnItem">ID: {{item.ID}}</li>
    <li class="list-group-item" *ngFor="let item of returnItem">Nombre: </li>
    <li class="list-group-item" *ngFor="let item of returnItem">A third item</li>
    <li class="list-group-item" *ngFor="let item of returnItem">A fourth item</li>
    <li class="list-group-item" *ngFor="let item of returnItem">And a fifth one</li>
</ul>

<router-outlet></router-outlet>
